/// <reference types="@sapui5/ts-types-esm" />
import type { HeaderAction } from '.';
import type AvatarShape from 'sap/m/AvatarShape';
export interface GenericActions {
    [id: string]: HeaderAction;
}
/**
 * Header
 * @isViewNode true
 */
export interface ObjectPageHeader<ACT = GenericActions> {
    /**
     * Set showRelatedApps to true to show the navigation button for related apps.
     */
    showRelatedApps?: boolean;
    /**
     * Set editableHeaderContent to true to make the header fields editable.
     */
    editableHeaderContent?: boolean;
    /**
     * Set simpleHeaderFacets to true to use simpler header facets.
     */
    simpleHeaderFacets?: boolean;
    /**
     * With displayShape you can define the shape of the avatar.
     */
    displayShape?: AvatarShape;
    /**
     * Set properties of actions on Object Page Header
     */
    actions?: ACT;
}
